mapper.insert:
INSERT INTO t_comment
(m_pk, c_seq, u_pk, c_content)
SELECT #{m_pk}, IFNULL(MAX(c_seq), 0) + 1, #{u_pk}, #{c_content}
FROM t_comment
WHERE m_pk = #{m_pk}

mapper.update:
UPDATE t_comment
SET c_content = #{c_content}
WHERE m_pk = #{m_pk}
AND c_seq = #{c_seq}
AND u_pk = #{u_pk}

UPDATE matzip
SET m_commentCount = m_commentCount+1
WHERE m_pk = #{m_pk}

mapper.delete:
DELETE FROM t_comment
WHERE m_pk = #{m_pk}
AND c_seq = #{c_seq}
AND u_pk = #{u_pk}

mapper.update:
UPDATE matzip
SET m_commentCount = m_commentCount-1
WHERE m_pk = #{m_pk}
