mapper.select:
SELECT M.*, ifnull(R.c_content, '') AS c_content, R.u_Nm AS u_nm , R.u_profile AS u_profile, DATE_FORMAT(R.c_regDate, '%Y-%m-%d') as c_regDate , R.u_pk FROM matzip M LEFT JOIN ( SELECT A.m_pk, A.c_content, C.u_Nm , C.u_profile ,A.c_regDate , A.u_pk FROM t_comment A INNER JOIN ( SELECT m_pk, MAX(c_seq) AS c_seq FROM t_comment GROUP BY m_pk ) B ON A.m_pk = B.m_pk AND A.c_seq = B.c_seq LEFT JOIN t_user C ON A.u_pk = C.u_pk ) R ON M.m_pk = R.m_pk
where m_title like '%' #{searchText} '%' or m_viewDetail like '%' #{searchText} '%' or m_category like '%' #{searchText} '%' or m_location like '%' #{searchText} '%'
where m_title like '%' #{searchText} '%' or m_viewDetail like '%' #{searchText} '%' or m_category like '%' #{searchText} '%' or m_location like '%' #{searchText} '%' and m_category = #{m_category}
where m_category = #{m_category}
order by M.m_starPoint desc limit #{sIdx}, #{rowCnt}

select count(*) from matzip
where m_title like '%' #{searchText} '%' or m_viewDetail like '%' #{searchText} '%' or m_category like '%' #{searchText} '%' or m_location like '%' #{searchText} '%'
where m_title like '%' #{searchText} '%' or m_viewDetail like '%' #{searchText} '%' or m_category like '%' #{searchText} '%' or m_location like '%' #{searchText} '%' and m_category = #{m_category}
where m_category = #{m_category}

mapper.update:
update matzip 
set m_viewCount = m_viewCount+1
where m_pk = #{m_pk}

mapper.select:
select * from t_like
where u_pk = #{u_pk} 
limit 0, #{listCount}

select * from t_like
where u_pk = #{u_pk} 
and m_pk = #{m_pk}
